package Assignmnet;

public class For_Loop_Even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int i;
     System.out.println("Even Number between 1 to 100 are :");
     for (i=1;i<=100;i++) {
    	 
    	 if (i%2==0) {
    		 System.out.println(i) ;  	 }
    	 
     }
	}

}
